{"command": "node -e "
const {ethers} = require('ethers');
const p = new ethers.JsonRpcProvider('https://mainnet.base.org');
const registry = new ethers.Contract(
  '0xcc5976790d41B10c254Be22d95F5ae20F73d0d8b',
  ['event Registered(address indexed ethAddress, string githubUsername)'],
  p
);
registry.queryFilter(registry.filters.Registered(), -10000, 'latest').then(events => {
  console.log('Registration events:', events.length);
  events.forEach(e => console.log('  -', e.args.githubUsername, '->', e.args.ethAddress));
});
""}

