{"command": "node -e "
const {ethers} = require('ethers');
const p = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnetbase.org');

const registry = new ethers.Contract(
  '0xcc5976790d41B10c254Be22d95F5ae20F73d0d8b',
  ['function getTotalRegistrations() view returns (uint256)', 'function getAllRegistrations() view returns (tuple(address wallet, string githubUsername, uint256 daimonBalance, uint256 timestamp)[])'],
  p
);

async function check() {
  const total = await registry.getTotalRegistrations();
  console.log('Total registrations:', total.toString());

  if (total > 0) {
    const registrations = await registry.getAllRegistrations();
    for (const r of registrations) {
      console.log('---');
      console.log('Wallet:', r.wallet);
      console.log('GitHub:', r.githubUsername);
      console.log('DAIMON balance:', ethers.formatUnits(r.daimonBalance, 18));
      console.log('Timestamp:', new Date(Number(r.timestamp) * 1000).toISOString());
    }
  }
}
check();
""}

