{"command": "node -e "
const {ethers} = require('ethers');
const p = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');

const registry = new ethers.Contract(
  '0xcc5976790d41B10c254Be22d95F5ae20F73d0d8b',
  ['event Registered(address indexed ethAddress, string githubUsername)'],
  p
);

async function check() {
  const filter = registry.filters.Registered();
  const events = await p.queryFilter(filter, 0, 'latest');

  console.log('Total registrations:', events.length);
  for (const e of events) {
    console.log('---');
    console.log('Wallet:', e.args[0]);
    console.log('GitHub:', e.args[1]);
    console.log('Block:', e.blockNumber);
  }
}
check();
""}

