async function main() {
  // Check the V2 pair
  const pairAddress = '0xBc3231036Ee1ECa03E5F67FEceDC640D21610823';
  const pairAbi = [
    'function getReserves() view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast)',
    'function token0() view returns (address)',
    'function token1() view returns (address)'
  ];

  const pair = new ethers.Contract(pairAddress, pairAbi, p);
  const [reserves, token0, token1] = await Promise.all([
    pair.getReserves(),
    pair.token0(),
    pair.token1()
  ]);

  console.log('V2 Pair:', pairAddress);
  console.log('Token0:', token0);
  console.log('Token1:', token1);
  console.log('Reserve0:', ethers.formatUnits(reserves[0], token0.toLowerCase() === USDC.toLowerCase() ? 6 : 18));
  console.log('Reserve1:', ethers.formatUnits(reserves[1], token1.toLowerCase() === DIEM.toLowerCase() ? 18 : 18));

  // Calculate expected output for 500 USDC
  // Using x * y = k formula: amountOut = (amountIn * 997 * reserveOut) / (reserveIn * 1000 + amountIn * 997)
  const amountIn = ethers.parseUnits('500', 6);
  const reserveIn = token0.toLowerCase() === USDC.toLowerCase() ? reserves[0] : reserves[1];
  const reserveOut = token0.toLowerCase() === USDC.toLowerCase() ? reserves[1] : reserves[0];

  const amountOut = (amountIn * 997n * reserveOut) / (reserveIn * 1000n + amountIn * 997n);
  console.log('\\nExpected output for 500 USDC:', ethers.formatUnits(amountOut, 18), 'DIEM');
}

main();
""}

