const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');
const wallet = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, provider);
const usdcAddress = '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913';
const diemAddress = '0x06B60479B3514F9d62126F13853b7996ee7A0a04';
const daimonAddress = '0x98c51C8E958ccCD37F798b2B9332d148E2c05D57';

async function main() {
  const ethBal = await provider.getBalance(wallet.address);
  const usdc = new ethers.Contract(usdcAddress, ['function balanceOf(address) view returns (uint256)'], provider);
  const diem = new ethers.Contract(diemAddress, ['function balanceOf(address) view returns (uint256)'], provider);
  const daimon = new ethers.Contract(daimonAddress, ['function balanceOf(address) view returns (uint256)'], provider);

  const usdcBal = await usdc.balanceOf(wallet.address);
  const diemBal = await diem.balanceOf(wallet.address);
  const daimonBal = await daimon.balanceOf(wallet.address);

  console.log('ETH:', ethers.formatEther(ethBal));
  console.log('USDC:', ethers.formatUnits(usdcBal, 6));
  console.log('DIEM:', ethers.formatEther(diemBal));
  console.log('DAIMON:', ethers.formatEther(daimonBal));
}
main();
""}

