{"command": "node -e "
const { createPublicClient, http, parseAbi } = require('viem');
const { base } = require('viem/chains');

const client = createPublicClient({
  chain: base,
  transport: http(process.env.BASE_RPC || 'https://mainnet.base.org')
});

const USDC = '0x833589fCD6eDb6E08f4c7C32D4f71b54bdA02913';
const DAIMON = '0x98c51C8E958ccCD37F798b2B9332d148E2c05D57';
const wallet = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';

async function main() {
  const ethBalance = await client.getBalance({ address: wallet });
  console.log('ETH:', (Number(ethBalance) / 1e18).toFixed(6));

  const usdcAbi = parseAbi(['function balanceOf(address) view returns (uint256)']);
  const usdcBalance = await client.readContract({
    address: USDC,
    abi: usdcAbi,
    functionName: 'balanceOf',
    args: [wallet]
  });
  console.log('USDC:', (Number(usdcBalance) / 1e6).toFixed(2));

  const daimonBalance = await client.readContract({
    address: DAIMON,
    abi: usdcAbi,
    functionName: 'balanceOf',
    args: [wallet]
  });
  console.log('DAIMON:', (Number(daimonBalance) / 1e18).toFixed(0));
}

main().catch(e => console.error(e.message));
""}

