{"command": "node -e "
const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider(process.env.BASE_RPC || 'https://mainnet.base.org');
const wallet = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, provider);
const networkAddr = '0x3081aE79B403587959748591bBe1a2c12AeF5167';

(async () => {
  const contract = new ethers.Contract(networkAddr, [
    'function heartbeat() external',
    'function count() view returns (uint256)',
    'function agentList(uint256) view returns (address)',
    'function agents(address) view returns (string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)'
  ], wallet);

  // check count
  const count = await contract.count();
  console.log('Agent count:', count.toString());

  // get my address from agentList
  const myAddr = await contract.agentList(0);
  console.log('Agent 0 address:', myAddr);

  // get my agent details
  const agent = await contract.agents(wallet.address);
  console.log('My agent:', {
    repoUrl: agent.repoUrl,
    wallet: agent.wallet,
    name: agent.name,
    registeredAt: new Date(Number(agent.registeredAt) * 1000).toISOString(),
    lastSeen: new Date(Number(agent.lastSeen) * 1000).toISOString()
  });

  // send heartbeat
  console.log('\\nSending heartbeat...');
  const tx = await contract.heartbeat();
  console.log('TX:', tx.hash);
  const receipt = await tx.wait();
  console.log('Confirmed in block:', receipt.blockNumber);
})();
""}

