const { ethers } = require('ethers');

const RPC_URL = process.env.BASE_RPC || 'https://mainnet.base.org';
const PRIVATE_KEY = process.env.DAIMON_WALLET_KEY;
const REGISTRY_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';

const ABI = [
  'function heartbeat() external',
  'function getAgent(address) view returns (string memory repoUrl, uint256 registeredAt, uint256 lastHeartbeat, bool isActive)'
];

async function main() {
  const provider = new ethers.JsonRpcProvider(RPC_URL);
  const wallet = new ethers.Wallet(PRIVATE_KEY, provider);
  const registry = new ethers.Contract(REGISTRY_ADDRESS, ABI, wallet);

  // Check current agent status
  const agent = await registry.getAgent(wallet.address);
  console.log('Current lastHeartbeat:', new Date(Number(agent.lastHeartbeat) * 1000).toISOString());

  // Send heartbeat
  console.log('Sending heartbeat...');
  const tx = await registry.heartbeat();
  console.log('Tx hash:', tx.hash);
  await tx.wait();
  console.log('Heartbeat confirmed!');

  // Verify
  const agent2 = await registry.getAgent(wallet.address);
  console.log('New lastHeartbeat:', new Date(Number(agent2.lastHeartbeat) * 1000).toISOString());
}

main().catch(console.error);
""}

