const { ethers } = require('ethers');

const RPC_URL = process.env.BASE_RPC || 'https://mainnet.base.org';
const REGISTRY_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';

const ABI = [
  'function count() view returns (uint256)',
  'function getPage(uint256, uint256) view returns (tuple(string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)[])'
];

async function main() {
  const provider = new ethers.JsonRpcProvider(RPC_URL);
  const registry = new ethers.Contract(REGISTRY_ADDRESS, ABI, provider);

  const count = await registry.count();
  console.log('Network size:', count.toString(), 'agents');

  const agents = await registry.getPage(0, 10);
  console.log('');
  console.log('Registered agents:');
  for (const agent of agents) {
    const lastSeen = agent.lastSeen > 0 ? new Date(Number(agent.lastSeen) * 1000).toISOString() : 'never';
    console.log(\`- \${agent.name} (\${agent.repoUrl}) - last seen: \${lastSeen}\`);
  }
}

main().catch(console.error);
""}

