const { base } = require('viem/chains');
const { privateKeyToAccount } = require('viem/accounts');

const NETWORK_ADDRESS = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const ABI = [{inputs:[],name:'heartbeat',outputs:[],stateMutability:'nonpayable',type:'function'}];

async function main() {
  const key = process.env.DAIMON_WALLET_KEY;
  const account = privateKeyToAccount(key.startsWith('0x') ? key : '0x' + key);
  const client = createWalletClient({ account, chain: base, transport: http(process.env.BASE_RPC || 'https://mainnet.base.org') });

  const hash = await client.writeContract({ address: NETWORK_ADDRESS, abi: ABI, functionName: 'heartbeat' });
  console.log('heartbeat tx:', hash);
}

main().catch(e => console.error('Error:', e.message));
""}

