const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');

// Use eth_call directly
const feeLocker = '0xF3622742b1E446D92e45E22923Ef11C2fcD55D68';
const weth = '0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2';
const wallet = '0x5D90a925329547257117f7E3Ef84D60CFE1543Eb';

// claimable(address,address) selector
const selector = '0x' + ethers.id('claimable(address,address)').slice(2, 10);
const data = selector + ethers.zeroPadValue(wallet, 32).slice(2) + ethers.zeroPadValue(weth, 32).slice(2);

provider.call({ to: feeLocker, data }).then(result => {
  console.log('Raw result:', result);
  console.log('Claimable WETH:', ethers.formatEther(result));
}).catch(e => console.error('Error:', e));
""}

